<script setup lang="ts">
  import HStack from '@/components/layout/HStack.vue'
  import InfoCard from '@/components/header/InfoCard.vue'
  import {computed, onMounted, onUnmounted, ref} from 'vue'

  // Days Since J2000
  const daysSinceJ2000 = computed(() => {
    const j2000 = new Date('2000-01-01T12:00:00Z')
    const now = new Date()
    const msPerDay = 1000 * 60 * 60 * 24
    return ((now.getTime() - j2000.getTime()) / msPerDay).toFixed(4)
  })

  // Solar Date
  const solarDate = computed(() => {
    return (parseFloat(daysSinceJ2000.value) + 44444.2156).toFixed(4)
  })

  // Coordinated Time (Live)
  const coordinatedTime = ref('')

  const updateCoordinatedTime = () => {
    const now = new Date()
    const hh = now.getUTCHours().toString().padStart(2, '0')
    const mm = now.getUTCMinutes().toString().padStart(2, '0')
    const ss = now.getUTCSeconds().toString().padStart(2, '0')
    coordinatedTime.value = `${hh}:${mm}:${ss}`
  }

  onMounted(() => {
    updateCoordinatedTime() // Initial
    const interval = setInterval(updateCoordinatedTime, 1000)
    onUnmounted(() => clearInterval(interval))
  })

  const moonPhase = computed(() => {
    const now = new Date()
    const year = now.getUTCFullYear()
    const month = now.getUTCMonth() + 1 // JS months are 0-based
    const day = now.getUTCDate()

    let r = year % 100
    r %= 19
    if (r > 9) r -= 19
    r = ((r * 11) % 30) + month + day
    if (month < 3) r += 2
    r -= (year < 2000) ? 4 : 8.3
    r = Math.floor(r + 0.5) % 30

    if (r < 0) r += 30

    if (r < 1) return 'ðŸŒ‘ New Moon'
    if (r < 7) return 'ðŸŒ’ Waxing Crescent'
    if (r < 10) return 'ðŸŒ“ First Quarter'
    if (r < 15) return 'ðŸŒ” Waxing Gibbous'
    if (r < 17) return 'ðŸŒ• Full Moon'
    if (r < 22) return 'ðŸŒ– Waning Gibbous'
    if (r < 25) return 'ðŸŒ— Last Quarter'
    if (r < 30) return 'ðŸŒ˜ Waning Crescent'

    return 'ðŸŒ‘ New Moon' // fallback
  })
</script>

